(function() {
  'use strict';

  angular.module('socialbase', ['ngRoute']).config(['$routeProvider', '$locationProvider', function($routeProvider, $locationProvider) {
    $locationProvider.html5Mode(true);
    $routeProvider.when('/', {
      templateUrl: 'partials/index.html',
      controller: 'IndexController'
    });

    $routeProvider.otherwise({
      redirectTo: '/'
    });

  }]);

  window.socialBaseAngular = angular.module('socialbase');
}());

(function() {
  'use strict';
  socialBaseAngular.controller('IndexController', ['$scope', IndexController]);

  function IndexController($scope) {
    function init() {
      setVariables();
    }

    function setVariables() {
      $scope.title = 'TODO-List';
      $scope.isList = true;
      $scope.todoListElements = [1,2,3];
    }

    init();
  }
}());

(function() {
  'use strict';
  socialBaseAngular.directive('pageDefault', [todoListDirective]);

  function todoListDirective() {
    return {
      restrict: 'E',
      transclude: true,
      scope: {
        title: '@',
        isList: '=list'
      },
      templateUrl: 'js/directives/pages/PageDefaultView.html',
      link: function(scope, params, algo) {

      }
    };
  }
}());

(function() {
  'use strict';
  socialBaseAngular.directive('todoListItem', [todoListItem]);

  function todoListItem() {
    return {
      restrict: 'E',
      scope: {
        listItem: '=list'
      },
      templateUrl: 'js/directives/todo/item/TodoListItemView.html',
      link: function(scope, params, algo) {
        
      }
    };
  }
}());

(function() {
  'use strict';
  socialBaseAngular.directive('menuLeft', [menuLeftDirective]);

  function menuLeftDirective() {
    return {
      restrict: 'E',
      templateUrl: 'js/directives/menus/MenuLeftView.html',
      link: function(scope, params, algo) {

      }
    };
  }
}());
